CREATE TABLE IF NOT EXISTS `el_about` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cn_name` varchar(18) NOT NULL COMMENT '中文名字',
  `en_name` varchar(18) NOT NULL COMMENT '英文名字',
  `introduce` text NOT NULL COMMENT '简介',
  `pic` varchar(255) NOT NULL DEFAULT '' COMMENT '头像',
  `hobby` text NOT NULL COMMENT '我的爱好',
  `qq` varchar(255) NOT NULL DEFAULT '' COMMENT 'QQ',
  `weixin` varchar(255) NOT NULL DEFAULT '' COMMENT '微信',
  `weibo` varchar(255) NOT NULL DEFAULT '' COMMENT '微博',
  `twitter` varchar(255) NOT NULL DEFAULT '' COMMENT '推特',
  `sina` varchar(255) NOT NULL DEFAULT '' COMMENT '新浪',
  `souhu` varchar(255) NOT NULL DEFAULT '' COMMENT '搜狐',
  `github` varchar(255) NOT NULL DEFAULT '' COMMENT 'GitHub',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='关于我表' AUTO_INCREMENT=2 ;

