CREATE TABLE IF NOT EXISTS `el_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章ID',
  `title` varchar(60) NOT NULL DEFAULT '' COMMENT '文章标题',
  `cat_id` smallint(5) unsigned NOT NULL COMMENT '文章分类ID',
  `author` varchar(30) NOT NULL DEFAULT '' COMMENT '文章作者',
  `thumbnail` varchar(255) NOT NULL DEFAULT '' COMMENT '文章图片',
  `keywords` varchar(180) NOT NULL DEFAULT '' COMMENT '文章关键词',
  `description` varchar(200) NOT NULL DEFAULT '' COMMENT '文章描述',
  `content` text NOT NULL COMMENT '文章内容',
  `hitcount` int(10) NOT NULL DEFAULT '0' COMMENT '文章点击次数',
  `is_show` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '是否显示：1：显示，0：不显示',
  `is_recom` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '是否推荐：1：推荐，0：不推荐',
  `created_at` int(11) NOT NULL DEFAULT '0' COMMENT '添加的时间',
  `updated_at` int(11) NOT NULL DEFAULT '0' COMMENT '修改的时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='文章表' AUTO_INCREMENT=21 ;

