--
-- 表的结构 `el_config`
--

CREATE TABLE IF NOT EXISTS `el_config` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '配置ID',
  `name` varchar(60) NOT NULL COMMENT '配置名称',
  `title` varchar(60) NOT NULL COMMENT '配置标题',
  `content` text NOT NULL COMMENT '配置内容',
  `field_type` varchar(50) NOT NULL COMMENT '配置表单类型',
  `field_value` varchar(50) NOT NULL COMMENT '配置表单类型',
  `descs` varchar(60) NOT NULL DEFAULT '' COMMENT '配置说明',
  `orders` int(11) NOT NULL DEFAULT '0' COMMENT '配置排序',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='网站配置表' AUTO_INCREMENT=13 ;

